import React, { useState } from 'react';

const CommentSection = ({ comments, onAddComment }) => {
  const [newComment, setNewComment] = useState('');
  const [localComments, setLocalComments] = useState(comments);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    try {
      const addedComment = await onAddComment(newComment); // Предполагаем, что onAddComment возвращает новый комментарий
      setLocalComments((prevComments) => [...prevComments, addedComment]); // Обновляем локальный список комментариев
      setNewComment(''); // Очищаем поле ввода
    } catch (error) {
      console.error('Failed to add comment:', error);
    }
  };

  return (
    <div>
      <h4>Comments</h4>
      <ul>
        {localComments.map((comment) => (
          <li key={comment.commentid}>
            <strong>{comment.username}:</strong> {comment.commenttext}
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <textarea
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Write a comment..."
          style={styles.textarea}
        />
        <button type="submit" style={styles.button}>Post</button>
      </form>
    </div>
  );
};

const styles = {
  textarea: { width: '100%', height: '50px', marginBottom: '10px', resize: 'none' },
  button: { padding: '5px 10px', border: 'none', backgroundColor: '#007BFF', color: '#fff' },
};

export default CommentSection;
