import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchGames, fetchGenres, addGame, deleteGame, getUserProfile } from '../services/api';
import '../styles/AdminPage.css';

const AdminPage = () => {
  const [games, setGames] = useState([]);
  const [editingGame, setEditingGame] = useState(null);
  const [isEditModalVisilble, setIsEditModalVisible] = useState(false);
  const [profile, setProfile] = useState(null); // Изначально null, чтобы проверить, когда данные загружены
  const [loading, setLoading] = useState(true); // Для управления состоянием загрузки
  const [genres, setGenres] = useState([]);
  const navigate = useNavigate();
  const [newGame, setNewGame] = useState({
    title: '',
    genre: '',
    releaseDate: '',
    developer: '',
    publisher: '',
    platform: '',
    description: '',
    coverImage: '',
  });

  const platforms = ['PC', 'PlayStation', 'XBox', 'Nintendo'];

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }

    // Загружаем профиль пользователя
    getUserProfile()
      .then((response) => {
        setProfile(response.data);
        if (response.data.role !== 'admin') {
          navigate('/profile'); // Перенаправляем, если не админ
        }
      })
      .catch((error) => {
        console.error('Error fetching profile:', error);
        navigate('/'); // Перенаправляем в случае ошибки
      })
      .finally(() => {
        setLoading(false); // Завершаем загрузку
      });
  }, [navigate]);

  useEffect(() => {
    // Загружаем список игр только после загрузки профиля и если пользователь админ
    if (profile?.role === 'admin') {
      fetchGames()
        .then((response) => setGames(response.data))
        .catch((error) => console.error('Error fetching games:', error));

      fetchGenres()
        .then((response) => setGenres(response))
        .catch((error) => console.error('Error fetching genres:', error));
    }
  }, [profile]);

  const handleAddGame = async () => {
    try {
      const addedGame = await addGame(newGame);
      setGames((prevGames) => [...prevGames, addedGame]);
      setNewGame({
        title: '',
        genre: '',
        releaseDate: '',
        developer: '',
        publisher: '',
        platform: '',
        description: '',
        coverImage: '',
      });
    } catch (error) {
      console.error('Error adding game:', error);
    }
  };

  const handleEditGame = (game) => {
    setEditingGame(game);
    setIsEditModalVisible(true);
  };

  const handleSaveChanges = async () => {
    try {
      const updatedGame = await addGame(editingGame);
      setGames((prevGames) => 
        prevGames.map((g) => (g.gameid === updatedGame.gameid ? updatedGame : g)));
      setEditingGame(null);
      setIsEditModalVisible(false);
    } catch (error) {
      console.error('Error saving changes:', error);
    }
  };

  const handleDeleteGame = async (gameId) => {
    try {
      await deleteGame(gameId);
      setGames((prevGames) => prevGames.filter((game) => game.gameid !== gameId));
    } catch (error) {
      console.error('Error deleting game:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewGame((prevGame) => ({ ...prevGame, [name]: value }));
  };

  if (loading) {
    return <div>Loading...</div>; // Показываем индикатор загрузки
  }

  if (profile?.role !== 'admin') {
    return null; // Если пользователь не админ, ничего не показываем
  }

  return (
    <div className="admin-page">
      <h1 className="admin-title">Admin Page</h1>
      <h2 className="section-title">Manage Games</h2>
      <div className="form-container">
        <h3>Add New Game</h3>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newGame.title}
          onChange={handleInputChange}
          className="input-field"
        />
        <select
          name="genre"
          value={newGame.genre}
          onChange={handleInputChange}
          className="input-field"
        >             л                                                                                                                                                                      
          <option value="">Select Genre</option>
          {genres.map((genre) => (
            <option key={genre.genreid} value={genre.genreid}>
              {genre.genrename}
            </option>
          ))}
        </select>
        <input
          type="date"
          name="releaseDate"
          value={newGame.releaseDate}
          onChange={handleInputChange}
          className="input-field"
        />
        <input
          type="text"
          name="developer"
          placeholder="Developer"
          value={newGame.developer}
          onChange={handleInputChange}
          className="input-field"
        />
        <input
          type="text"
          name="publisher"
          placeholder="Publisher"
          value={newGame.publisher}
          onChange={handleInputChange}
          className="input-field"
        />
        <select
          name="platform"
          value={newGame.platform}
          onChange={handleInputChange}
          className="input-field"
        >
          <option value="">Select Platform</option>
          {platforms.map((platform) => (
            <option key={platform} value={platform}>
              {platform}
            </option>
          ))}
        </select>
        <textarea
          name="description"
          placeholder="Description"
          value={newGame.description}
          onChange={handleInputChange}
          className="input-field"
        ></textarea>
        <input
          type="text"
          name="coverImage"
          placeholder="Cover Image URL"
          value={newGame.coverImage}
          onChange={handleInputChange}
          className="input-field"
        />
        <button onClick={handleAddGame} className="add-button">Add Game</button>
      </div>
      <h3 className="section-title">Existing Games</h3>
      <ul className="game-list">
        {games.map((game) => (
          <li key={game.gameid} className="game-item">
            {game.title}{' '}
            <button onClick={() => handleDeleteGame(game.gameid)} className="delete-button">
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPage;
