import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token); // true, если токен есть
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    alert('You have been logged out!');
    navigate('/login');
  };
  
  return (
    <nav style={styles.navbar}>
      <Link to="/" style={styles.link}>Home</Link>
      <Link to="/profile" style={styles.link}>Profile</Link>
      <Link to="/register" style={styles.link}>Register</Link>
      <Link to="/login" style={styles.link}>Login</Link>
      <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
      <p style={styles.move_right}>Welcome back, {}</p>
    </nav>
  );
};

const styles = {
  navbar: { display: 'flex', gap: '15px', padding: '10px', background: '#333', color: '#fff' },
  link: { color: '#fff', textDecoration: 'none' },
  logoutButton: { background: 'none', border: 'none', color: '#fff', textDecoration: 'none', cursor: 'pointer' },
  move_right: { display: 'flex' }
};

export default Navbar;
