import React, { useState } from 'react';
import axios from 'axios';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Отправка данных на сервер
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });
      console.log('Login successful:', response.data);

      // Сохранение токена или других данных в localStorage (или в state)
      localStorage.setItem('authToken', response.data.token);

      // Перенаправление на другую страницу после успешной авторизации
      window.location.href = '/home'; // Укажите ваш роут
    } catch (error) {
      console.error('Login error:', error.response?.data || error.message);
      setErrorMessage(error.response?.data?.error || 'Ошибка авторизации');
    }
  };

  return (
    <div style={styles.container}>
      <h2>Вход</h2>
      {errorMessage && <p style={styles.error}>{errorMessage}</p>}
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={styles.input}
          />
        </div>
        <div style={styles.formGroup}>
          <label htmlFor="password">Пароль:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={styles.input}
          />
        </div>
        <button type="submit" style={styles.button}>
          Войти
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: { maxWidth: '400px', margin: '0 auto', padding: '20px', textAlign: 'center' },
  form: { display: 'flex', flexDirection: 'column', gap: '10px' },
  formGroup: { textAlign: 'left' },
  input: { width: '100%', padding: '10px', marginBottom: '10px', borderRadius: '5px', border: '1px solid #ccc' },
  button: { padding: '10px 20px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', borderRadius: '5px', cursor: 'pointer' },
  error: { color: 'red' },
};

export default LoginPage;
