import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchGames, addGame, deleteGame, getUserProfile } from '../services/api';

const AdminPage = () => {
  const [games, setGames] = useState([]);
  const [profile, setProfile] = useState();
  const navigate = useNavigate();
  const [newGame, setNewGame] = useState({
    title: '',
    genre: '',
    releaseDate: '',
    developer: '',
    publisher: '',
    platform: '',
    description: '',
    coverImage: '',
  });

  useEffect(() => {
    const token = localStorage.getItem('token');

    if(!token){
      navigate('/');
    }
    else{
      getUserProfile().then((response) => setProfile(response.data));
    }

    console.log(profile);


    fetchGames().then((response) => setGames(response.data));
  }, [navigate]);

  const handleAddGame = async () => {
    try {
      const addedGame = await addGame(newGame);
      setGames((prevGames) => [...prevGames, addedGame]);
      setNewGame({
        title: '',
        genre: '',
        releaseDate: '',
        developer: '',
        publisher: '',
        platform: '',
        description: '',
        coverImage: '',
      });
    } catch (error) {
      console.error('Error adding game:', error);
    }
  };

  const handleDeleteGame = async (gameId) => {
    try {
      await deleteGame(gameId);
      setGames((prevGames) => prevGames.filter((game) => game.gameid !== gameId));
    } catch (error) {
      console.error('Error deleting game:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewGame((prevGame) => ({ ...prevGame, [name]: value }));
  };

  return (
    <div>
    {(profile !== 'admin') &&
    <div>
      <h1>Admin Page</h1>
      <h2>Manage Games</h2>
      <div>
        <h3>Add New Game</h3>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newGame.title}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="genre"
          placeholder="Genre"
          value={newGame.genre}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="releaseDate"
          value={newGame.releaseDate}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="developer"
          placeholder="Developer"
          value={newGame.developer}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="publisher"
          placeholder="Publisher"
          value={newGame.publisher}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="platform"
          placeholder="Platform"
          value={newGame.platform}
          onChange={handleInputChange}
        />
        <textarea
          name="description"
          placeholder="Description"
          value={newGame.description}
          onChange={handleInputChange}
        ></textarea>
        <input
          type="text"
          name="coverImage"
          placeholder="Cover Image URL"
          value={newGame.coverImage}
          onChange={handleInputChange}
        />
        <button onClick={handleAddGame}>Add Game</button>
      </div>
      <h3>Existing Games</h3>
      <ul>
        {games.map((game) => (
          <li key={game.gameid}>
            {game.title}{' '}
            <button onClick={() => handleDeleteGame(game.gameid)}>Delete</button>
          </li>
        ))}
      </ul>
      </div>
    };
    </div>
  );
};

export default AdminPage;
