import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Проверяем токен при первой загрузке компонента
    const checkLoginStatus = () => {
      const token = localStorage.getItem('token');
      setIsLoggedIn(!!token);
    };

    checkLoginStatus();

    // Добавляем слушателя для обновлений localStorage
    const handleStorageChange = () => checkLoginStatus();

    window.addEventListener('storage', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false); // Локально обновляем состояние
    alert('You have been logged out!');
    navigate('/login');
  };

  return (
    <nav style={styles.navbar}>
      <Link to="/" style={styles.link}>Home</Link>
      {<Link to="/profile" style={styles.link}>Profile</Link>}
      {!isLoggedIn && <Link to="/register" style={styles.link}>Register</Link>}
      {!isLoggedIn && <Link to="/login" style={styles.link}>Login</Link>}
      {isLoggedIn && <Link onClick={handleLogout} style={styles.link}>Logout</Link>}
      <h1>{isLoggedIn ? 'Welcome back!' : 'Please, log in!'}</h1>
    </nav>
  );
};

const styles = {
  navbar: { display: 'flex', 'flex-direction': 'row', 'justify-content': 'space-between', 'align-items': 'center', gap: '15px', 'padding-left': '70px', 'padding-right': '70px', background: '#333', color: '#fff', },
  link: { color: '#fff', textDecoration: 'none' },
  logoutButton: { background: 'none', border: 'none', color: '#fff', textDecoration: 'none', cursor: 'pointer' },
};

export default Navbar;
