import React, { useState } from "react";
import axios from "axios";

const GameRating = ({ gameId }) => {
  const [rating, setRating] = useState("");
  const [message, setMessage] = useState("");

  const handleRatingSubmit = async () => {
    if (!rating || rating < 1 || rating > 10) {
      setMessage("Пожалуйста, введите оценку от 1 до 10.");
      return;
    }

    try {
      await axios.post("/api/games/rate", { gameId, rating: parseInt(rating) });
      setMessage("Спасибо за вашу оценку!");
      setRating(""); // Очистка поля после успешной отправки
    } catch (error) {
      setMessage("Ошибка при отправке оценки. Попробуйте снова.");
    }
  };

  return (
    <div>
      <h3>Оцените эту игру:</h3>
      <input
        type="number"
        min="1"
        max="10"
        value={rating}
        onChange={(e) => setRating(e.target.value)}
        placeholder="Введите оценку (1-10)"
      />
      <button onClick={handleRatingSubmit}>Оценить</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default GameRating;
