import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUserProfile } from '../services/api';

const ProfilePage = () => {
  const [profile, setProfile] = useState();
  const [isLoggedIn, setIsLoggedIn] = useState(null);
  const navigate = useNavigate();
  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
    console.log(isLoggedIn);

    !isLoggedIn && navigate('/');

    !token && getUserProfile().then((response) => setProfile(response.data));
  }, [navigate]); 

  return (
    <div>
      <h1 style={styles.title}>Profile Page</h1>
      {profile && <h2 style={styles.title}>Welcome back, {profile.username}!</h2>}
    </div>
  );
};

const styles = { title: { display: 'flex', justifyContent : 'center', }
};

export default ProfilePage;
