import React from 'react';

const GameCard = ({ game }) => {
  return (
    <div key={game.GameID} style={styles.card}>
      <img src={game.CoverImage} alt={game.Title} style={styles.image} />
      <h3>{game.Title}</h3>
      <p>{game.Genre}</p>
      <p>Rating: {game.AverageRating || 'N/A'}</p>
    </div>
  );
};

const styles = {
  card: { border: '1px solid #ddd', padding: '15px', borderRadius: '8px', width: '200px' },
  image: { width: '100%', borderRadius: '8px' },
};

export default GameCard;
