import React, { useState, useEffect } from 'react';

const CommentSection = ({ comments, onAddComment }) => {
  const [newComment, setNewComment] = useState('');
  const [localComments, setLocalComments] = useState(comments); // Локальный список комментариев
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token); // Устанавливаем, авторизован ли пользователь
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return; // Предотвращаем отправку пустых комментариев

    try {
      const addedComment = await onAddComment(newComment); // Отправляем комментарий и получаем данные нового комментария
      setLocalComments((prevComments) => [...prevComments, addedComment]); // Обновляем локальный список
      setNewComment(''); // Очищаем поле ввода
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  return (
    <div>
      <h4>Comments</h4>
      <ul>
        {localComments.map((comment) => (
          <li key={comment.commentid}>
            <strong>{comment.username}:</strong> {comment.commenttext}
          </li>
        ))}
      </ul>

      {isLoggedIn ? (
        <form onSubmit={handleSubmit}>
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Write a comment..."
            style={styles.textarea}
          />
          <button type="submit" style={styles.button}>
            Post
          </button>
        </form>
      ) : (
        <p style={styles.notice}>Please log in to add a comment.</p>
      )}
    </div>
  );
};

const styles = {
  textarea: { width: '100%', height: '50px', marginBottom: '10px', resize: 'none' },
  button: { padding: '5px 10px', border: 'none', backgroundColor: '#007BFF', color: '#fff' },
  notice: { color: '#FF0000', fontStyle: 'italic' },
};

export default CommentSection;
