import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GameRating = () => {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState('');
  const [rating, setRating] = useState(0);
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Получение списка игр
    const fetchGames = async () => {
      try {
        const response = await axios.get('http://localhost:5000/games/games');
        setGames(response.data);
      } catch (error) {
        console.error('Ошибка загрузки игр', error);
      }
    };
    fetchGames();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/${selectedGame}/rate`, {
        userId: 1, // Заглушка для идентификатора пользователя
        rating,
      });
      setMessage(response.data.message || 'Рейтинг успешно добавлен!');
    } catch (error) {
      setMessage(error.response?.data?.message || 'Ошибка при добавлении рейтинга');
    }
  };

  return (
    <div>
      <h2>Оценить игру</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="game">Выберите игру:</label>
          <select
            id="game"
            value={selectedGame}
            onChange={(e) => setSelectedGame(e.target.value)}
            required
          >
            <option value="">-- Выберите --</option>
            {games.map((game) => (
              <option key={game.id} value={game.id}>
                {game.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="rating">Рейтинг (1-10):</label>
          <input
            type="number"
            id="rating"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            min="1"
            max="10"
            required
          />
        </div>
        <button type="submit">Отправить рейтинг</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default GameRating;
