import React, { useState } from 'react';
import CommentSection from './CommentSection';

const ReviewCard = ({ review, onAddComment }) => {
  const [commentsVisible, setCommentsVisible] = useState(false);

  const toggleComments = () => {
    setCommentsVisible(!commentsVisible);
  };

  const handleAddComment = async (reviewId, commentText) => {
    try {
      const newComment = await onAddComment(reviewId, commentText);
  
      // Добавляем новый комментарий в локальное состояние review.comments
      review.comments = [...review.comments, newComment];
  
      return newComment;
    } catch (error) {
      console.error('Error adding comment:', error);
      throw error;
    }
  };
  

  return (
    <div style={styles.card}>
      <h4>User: {review.username}</h4>
      <p>Rating: {review.rating}</p>
      <p>{review.reviewtext}</p>
      <button style={styles.button}>Like ({review.likes})</button>
      <button style={styles.toggleButton} onClick={toggleComments}>
        {commentsVisible ? 'Hide Comments' : 'Show Comments'}
      </button>

      {commentsVisible && (
        <CommentSection
          comments={review.comments}
          onAddComment={(commentText) => handleAddComment(review.reviewid, commentText)}
        />
      )}
    </div>
  );
};

const styles = {
  card: { border: '1px solid #ddd', padding: '10px', borderRadius: '8px', margin: '10px 0' },
  button: { marginTop: '10px', padding: '5px 10px', border: 'none', backgroundColor: '#007BFF', color: '#fff' },
  toggleButton: { marginLeft: '10px', padding: '5px 10px', border: 'none', backgroundColor: '#6c757d', color: '#fff' },
};

export default ReviewCard;
