import React, { useEffect, useState } from "react";
import axios from "axios";

const GameList = () => {
  const [games, setGames] = useState([]);
  const [rating, setRating] = useState("");

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const response = await axios.get("/api/games");
        setGames(response.data);
      } catch (error) {
        alert("Ошибка загрузки игр");
      }
    };
    fetchGames();
  }, []);

  const handleRateGame = async (gameId) => {
    try {
      await axios.post("/api/games/rate", { gameId, rating: parseInt(rating) });
      alert("Игра оценена!");
    } catch (error) {
      alert("Ошибка оценки");
    }
  };

  return (
    <div>
      <h1>Список игр</h1>
      <ul>
        {games.map((game) => (
          <li key={game.id}>
            <h3>{game.title}</h3>
            <p>{game.description}</p>
            <input
              type="number"
              min="1"
              max="10"
              placeholder="Оценка"
              onChange={(e) => setRating(e.target.value)}
            />
            <button onClick={() => handleRateGame(game.id)}>Оценить</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default GameList;
