import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ReviewCard from '../components/ReviewCard';
import { fetchGameDetails, fetchGameReviews } from '../services/api';

const GameDetailsPage = () => {
  const { id } = useParams();
  const [game, setGame] = useState({});
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    fetchGameDetails(id).then((response) => setGame(response.data));
    fetchGameReviews(id).then((response) => setReviews(response.data));
  }, [id]);

  return (
    <div>
      <h1>{game.title}</h1>
      <p>{game.description}</p>
      <h3>Reviews</h3>
      {reviews.map((review) => (
        <ReviewCard key={review.reviewid} review={review} onAddComment={handleAddComment} />
      ))}
    </div>
  );
};

export default GameDetailsPage;
