import React from 'react';

const GameCard = ({ game }) => {
  return (
    <div style={styles.card}>
      <img src={game.coverimage} alt={game.title} style={styles.image} />
      <h2>{game.title}</h2>
      <h3>Publisher: {game.publisher}</h3>
      <h3>Developer: {game.developer}</h3>
      <h4>{game.description}</h4>
      <p>Release Data: {game.releasedate}</p>
      <p>Genre: {game.genre}</p>
      <p>Rating: {game.AverageRating || 'N/A'}</p>
    </div>
  );
};

const styles = {
  card: { border: '1px solid #ddd', padding: '10px', borderRadius: '8px', width: '400px' },
  image: { width: '100%', borderRadius: '8px' },
};

export default GameCard;
